import axios from 'axios' //引入 axios
import baseUrl from '../api/baseUrl' //使用环境变量 + 模式的方式定义基础URL

// 创建 axios 实例
const service = axios.create({
  baseURL: baseUrl, // api 的 base_url
  timeout: 15000, // 请求超时时间
})
service.interceptors.request.use(function (config) {
  config.headers['version'] = 'v1.0';
  if (localStorage.getItem("user-token")) {
    config.headers['user-token'] = localStorage.getItem("user-token");
  }
  if (localStorage.getItem("access-token")) {
    config.headers['access-token'] = localStorage.getItem('access-token') || '';
  }
  config.headers['plat-form'] = 2;
  return config
}, function (error) {
  return Promise.reject(error)
})

// 添加请求拦截器，在请求头中加token
axios.interceptors.request.use(
  console.log("request"),
  (config) => {
    console.log("request2")
    if (localStorage.getItem('loginToken')) {
      console.log(localStorage.getItem('loginToken'))
      config.headers.Authorization = localStorage.getItem('loginToken');
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
});

export default service
