// 日期格式化 
// 从 Nov 9, 2022, 12:00:00 AM vi 变成 2022-11-9
import Vue from 'vue'

export function dataFormat(date) {
    var d=new Date(date); 
    d=d.getFullYear() + '-' + (d.getMonth() + 1) + '-' + d.getDate(); 
    return d;
  }
