import { login } from '@/api/loginApi'//引入登录 api 接口
import { getToken } from '@/utils/auth'
import user from './user'

const loginVo={
    state: {
        user:{  // 存储用户的 userId 和 username
            userId:'',
            username:''
        },  
        // 存储token
        loginToken: localStorage.getItem('loginToken') ? localStorage.getItem('loginToken') : ''
    },
    mutations:{
        updateLoginVo(state,user){
            state.user=user
        }
    },

    actions:{
        // 登录
        Login({ commit }, loginInfo) { //定义 Login 方法，在组件中使用 this.$store.dispatch("Login") 调用
            return new Promise((resolve, reject) => { //封装一个 Promise
            login(loginInfo).then(response => { //使用 login 接口进行网络请求
                // state.user=JSON.stringify(response.data.data)
                commit
                // localStorage.setItem("user",JSON.stringify(response.data.data))
                // localStorage.setItem("loginToken",JSON.stringify(response.data.data.loginToken))
                resolve(response) //将结果封装进 Promise
            }).catch(error => {
                reject(error)
            })
            })
        },
    }
}

export default loginVo
