import { login } from '@/api/loginApi'//引入登录 api 接口
import { getToken } from '@/utils/auth'


const loginVo={
    state: {
        userId: '',
        loginToken: '',  // 用户token，用户确认当前用户是否登录
    },
    getters: {
        userId: state => {
            let userId = state.userId;
            if(!userId){
                userId =  JSON.parse(window.sessionStorage.getItem('userId'));
            }
            return userId;
        },
        loginToken: state => {
            let loginToken = state.loginToken;
            if(!loginToken){
                loginToken = JSON.parse(window.sessionStorage.getItem('loginToken'));
            }
            return loginToken;
        },
    },

    actions:{
        // 登录
        Login({ commit }, loginInfo) { //定义 Login 方法，在组件中使用 this.$store.dispatch("Login") 调用
            return new Promise((resolve, reject) => { //封装一个 Promise
            login(loginInfo).then(response => { //使用 login 接口进行网络请求
                var user=JSON.stringify(response.data.data)
                commit('updateLoginVo',user)
                localStorage.setItem("userId",JSON.stringify(response.data.data.userId))
                localStorage.setItem("loginToken",JSON.stringify(response.data.data.loginToken))
                // localStorage.setItem("loginToken",JSON.stringify(response.data.data.loginToken))
                resolve(response) //将结果封装进 Promise
            }).catch(error => {
                reject(error)
            })
            })
        },
    }
}

export default loginVo
