import "core-js/modules/es.array.push.js";
import "core-js/modules/es.object.to-string.js";
import "core-js/modules/web.dom-collections.for-each.js";
export default {
  name: "Head",
  data: function data() {
    return {
      userId: JSON.parse(window.localStorage.getItem("userId")),
      nickName: '',
      roleNameList: []
    };
  },
  methods: {
    exitBtn: function exitBtn() {
      this.$router.push({
        path: "/login"
      });
      localStorage.removeItem("userId");
      localStorage.removeItem("loginToken");
      localStorage.removeItem("user");
    },
    getUserByUserId: function getUserByUserId() {
      var _this = this;
      this.$store.dispatch("GetUserByUserId", this.userId).then(function (res) {
        var statusCode = res.data.statusCode;
        if (statusCode == 200) {
          var user = JSON.parse(localStorage.getItem("user"));
          _this.nickName = user.nickName;
          user.roleList.forEach(function (item) {
            _this.roleNameList.push(item.roleName);
          });
        }
      });
    }
  },
  //mounted在页面渲染完成之后使用，也就是此时页面已完全取出Vue中的数据。
  mounted: function mounted() {
    if (this.userId) {
      this.getUserByUserId();
    }
  },
  computed: {
    test: function test() {
      return this.$store.state.loginVo.user.nickName;
    }
  }
};
