import Vue from 'vue'
import App from './App.vue'
import router from './router/index' //引入路由配置
import store from './store' //引入 Vuex 状态管理

/* 插件 */
import './plugins/element.js'  // element-ui
import './plugins/vxe-table.js' // vxe-table

/* 工具 */

// import {dateFormat} from './utils/dateUtil'  // 日期格式化
 
/*导入qs axios*/
import qs from 'qs';
import axios from 'axios'

// global css 全局样式 自己编写的
import './assets/styles/index.scss'
import './assets/styles/view.css'
import './assets/styles/table.css'

// font-awesome 样式和图标
import 'font-awesome/css/font-awesome.min.css'

// 阿里巴巴图标
import './assets/iconfont/iconfont.css'

// vue 函数库
import XEUtils from 'xe-utils'


Vue.config.productionTip = false

/*全局绑定*/
Vue.prototype.$qs = qs;
Vue.prototype.$axios =axios;
Vue.prototype.http=axios
Vue.config.productionTip = false
Vue.prototype.$XEUtils = XEUtils

// 添加请求拦截器，在请求头中加token
axios.interceptors.request.use(
  console.log("request"),
  (config) => {
    console.log("request2")
    if (localStorage.getItem('loginToken')) {
      console.log(localStorage.getItem('loginToken'))
      config.headers.Authorization = localStorage.getItem('loginToken');
    }
    return config;
  },
  error => {
    return Promise.reject(error);
});

new Vue({
  render: h => h(App),
  router, //使用路由配置
  store //使用 Vuex 进行状态管理
}).$mount('#app')
