import { getAllMenu } from '@/api/menu'//引入 获取菜单列表 api  接口

const menu = {
  action:{
      // 获取菜单列表
      GetAllMenu({ commit }) {
          return new Promise((resolve, reject) => { //封装一个 Promise
            getAllMenu().then(response => { //使用  getAllMenu 接口进行网络请求
              resolve(response) //将结果封装进 Promise
            }).catch(error => {
              console.log("menu.js ")
              reject(error)
            })
          })
      },
  }
}

export default menu
